</node>
<node CREATED="1614516769578" ID="ID_215071192" MODIFIED="1615906132823" TEXT="Seitenaufrufe">
<node CREATED="1614516529214" ID="ID_834559042" MODIFIED="1614516535605" TEXT="Der Seitenaufruf ist der popul&#xe4;rste"/>
<node CREATED="1614516805054" ID="ID_799906201" MODIFIED="1614516818388" TEXT="sobald die Seite aufgerufen wurde k&#xf6;nnen Daten &#xfc;bermittelt werden."/>
<node CREATED="1614516839316" ID="ID_480328530" MODIFIED="1614516847543" TEXT="N&#xe4;chste Grundeinheit oberhalb der Hits"/>
